import {useContext, useEffect, useState} from "react";
import { useParams, Link } from "react-router-dom";
import { itemServiceFactory } from '../../services/itemsService';
import {AuthContext} from "../../contexts/AuthContext";


export const ItemDetails = () => {
    const { itemId } = useParams();
    const [item, setItem] = useState({});
    const {userId} = useContext(AuthContext)
    const [username, setUsername] = useState('');

    // useEffect(() => {
    //     itemsService
    //         .getOne(item._id)
    //         .then((result) => {
    //             setItem(result);
    //         })
    // }, [itemId]);

    useEffect(() => {
        async function fetchData() {
            const response = await fetch(`http://localhost:3030/jsonstore/items/${itemId}`);
            console.log(response)
            const data = await response.json();
            setItem(data)
            // do something with the user data
        }
        fetchData();
    }, [itemId]);

    const onDeleteClick = async () => {
        await itemServiceFactory.delete(item._id)
    }

    return (
        <section id="game-details">
            <h1>Item Details</h1>
            <div className="info-section">
                <div className="game-header">
                    <img className="game-img" src={item.imageUrl} />
                    <h1>{item.title}</h1>
                    <span className="levels">MaxLevel: {item.maxLevel}</span>
                    <p className="type">{item.category}</p>
                </div>

                <p className="text">{item.summary}</p>

                {/* <!-- Edit/Delete buttons ( Only for creator of this game )  --> */}
                <div className="buttons">
                    <Link to={`/catalog/${item._id}/edit`} className="button">
                        Edit
                    </Link>
                    <Link to="/catalog" className="button" onClick={onDeleteClick}>
                        Delete
                    </Link>
                </div>
            </div>


        </section>
    );
};
