import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import * as itemsService from "../../services/itemsService";


export const ItemDetails = () => {
    const [username, setUsername] = useState("");
    const [comment, setComment] = useState("");
    const { itemId } = useParams();
    const [item, setItem] = useState({});

    useEffect(() => {
        itemsService
            .getOne(itemId)
            .then((result) => {
                setItem(result);
                // return commentService.getAll(gameId);
            })
        //   .then((result) => {
        //     setComments(result);
        //   });
    }, [itemId]);

    const onCommentSubmit = async (e) => {
        e.preventDefault();

        const result = await itemsService.addComment(itemId,{
            username,
            comment,
        });

        setItem(state => ({...state,comments: {...state.comments, [result._id]: result}}))
        setUsername("");
        setComment("");
    };

    return (
        <section id="game-details">
            <h1>Item Details</h1>
            <div className="info-section">
                <div className="game-header">
                    <img className="game-img" src={item.imageUrl} />
                    <h1>{item.title}</h1>
                    <span className="levels">MaxLevel: {item.maxLevel}</span>
                    <p className="type">{item.category}</p>
                </div>

                <p className="text">{item.summary}</p>

                <div className="details-comments">
                    <h2>Comments:</h2>
                    <ul>
                        {item.comments && Object.values(item.comments).map((x) => (
                            <li key={x._id} className="comment">
                                <p>
                                    {x.username}: {x.comment}
                                </p>
                            </li>
                        ))}
                    </ul>
                    {/* <!-- Display paragraph: If there are no games in the database --> */}
                </div>

                {/* <!-- Edit/Delete buttons ( Only for creator of this game )  --> */}
                <div className="buttons">
                    <a href="#" className="button">
                        Edit
                    </a>
                    <a href="#" className="button">
                        Delete
                    </a>
                </div>
            </div>

            {/* <!-- Bonus --> */}
            {/* <!-- Add Comment ( Only for logged-in users, which is not creators of the current game ) --> */}
            <article className="create-comment">
                <label>Add new comment:</label>
                <form className="form" onSubmit={onCommentSubmit}>
                    <input
                        type="text"
                        name="username"
                        placeholder="Пешо"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    ></input>
                    <textarea
                        name="comment"
                        placeholder="Comment......"
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                    ></textarea>
                    <input className="btn submit" type="submit" value="Add Comment" />
                </form>
            </article>
        </section>
    );
};
