import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import * as itemsService from "../../services/itemsService";


export const ItemDetails = () => {
    const { itemId } = useParams();
    const [item, setItem] = useState({});

    // useEffect(() => {
    //     itemsService
    //         .getOne(item._id)
    //         .then((result) => {
    //             setItem(result);
    //             // return commentService.getAll(gameId);
    //         })
    // }, [itemId]);

    useEffect(() => {
        async function fetchData() {
            const response = await fetch(`http://localhost:3030/jsonstore/items/${itemId}`);
            const data = await response.json();
            setItem(data)
            // do something with the user data
        }
        fetchData();
    }, [itemId]);

    const onDeleteClick = async () => {
        await itemsService.gameDeleting(item._id)
    }

    return (
        <section id="game-details">
            <h1>Item Details</h1>
            <div className="info-section">
                <div className="game-header">
                    <img className="game-img" src={item.imageUrl} />
                    <h1>{item.title}</h1>
                    <span className="levels">MaxLevel: {item.maxLevel}</span>
                    <p className="type">{item.category}</p>
                </div>

                <p className="text">{item.summary}</p>

                {/* <!-- Edit/Delete buttons ( Only for creator of this game )  --> */}
                <div className="buttons">
                    <a href="#" className="button">
                        Edit
                    </a>
                    <a href="/catalog" className="button" onClick={onDeleteClick}>
                        Delete
                    </a>
                </div>
            </div>


        </section>
    );
};
