import { Routes, Route, useNavigate } from "react-router-dom";
import { Header } from "./components/Header/Header";
import { Home } from "./components/Home/Home";
import { Login } from "./components/Login/Login";
import { Register } from "./components/Register/Register";
import { CreateItem } from "./components/CreateItem/CreateItem";
import { Catalog } from "./components/Catalog/Catalog";
import { Footer } from "./components/Footer/Footer";
import { ItemDetails } from "./components/ItemDetails/ItemDetails";
import { EditItem } from "./components/EditItem/EditItem";
import { useState, useEffect } from "react";
import * as itemService from "./services/itemsService"

function App() {
    const navigate = useNavigate()
    const [items, setItems] = useState([]);

    useEffect(() => {
        itemService.getAll().then(result => {
            setItems(result)
        })
    }, [])

    const onRegisterSubmit = async (values) => {
        const { confirmPassword, ...registerData } = values;
        if (confirmPassword !== registerData.password) {
            return;
        }

        try {
            const result = await authService.register(registerData);

            setAuth(result);

            navigate('/catalog');
        } catch (error) {
            console.log('There is a problem');
        }
    };

    const onCreateItemSubmit = async (data) => {

        const newGame = await itemService.create(data)

        setItems(state => [...state, newGame])
        navigate('/catalog')
    }

    const onItemEditSubmit = async (values) => {
        const result = await itemService.edit(values._id, values);

        setItems(state => state.map(x => x._id === values._id ? result : x))

        navigate(`/catalog/${values._id}`)
    }

  return (
    <>
      <div id="box">
        <Header />

        <main id="main-content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/create-item" element={<CreateItem onCreateItemSubmit={onCreateItemSubmit}/>} />
            <Route path="/catalog" element={<Catalog items={items}/>} />
            <Route path='/catalog/:itemId' element={<ItemDetails />} />
            <Route path="/catalog/:itemId/edit" element={<EditItem onItemEditSubmit={onItemEditSubmit}/>} />
          </Routes>

        </main>
      </div>
        <footer>
            <Footer />
        </footer>



    </>
  );
}

export default App
